                    icon: 'success',
                    title: 'Congrats!! Image Taken!',
                    text: 'You have succesfully uploaded your image!',
                    confirmButtonText: "OK"
                });
                this.props.history.push("/Login");
            }else{
                console.log("image upload failed");
            }
        }catch(e){

        }
    }

    render() {
      return (

 <div className ="container">
